				return 1;
				# 1 - undefined dep
			end if;
    end if;
    
	if dep_id > 0  then 
		return dep_id;
	else
		set dep_id = (select max(dep_id) from dep_has_worker  where worker_id=wrkID and (archive=0 or archive is null));
        if dep_id > 0  then 
			update worker_settings set last_dep=dep_id  where id=wrkID;
			return dep_id;
