-- -----------------------------------------------------
-- function SPLIT_STR
-- -----------------------------------------------------

DELIMITER $$
USE `kcson`$$
CREATE FUNCTION SPLIT_STR(
  x VARCHAR(4096),
  delim VARCHAR(100),
  pos INT
)
RETURNS VARCHAR(4096)
    DETERMINISTIC
    SQL SECURITY INVOKER
RETURN REPLACE(SUBSTRING(SUBSTRING_INDEX(x, delim, pos),
       LENGTH(SUBSTRING_INDEX(x, delim, pos -1)) + 1),
       delim, '');$$

DELIMITER ;

-- -----------------------------------------------------
-- function SPLIT_STR_ARR_LEN
-- -----------------------------------------------------

DELIMITER $$
USE `kcson`$$
CREATE  FUNCTION SPLIT_STR_ARR_LEN(
  x VARCHAR(4096),
  delim VARCHAR(100)
)
RETURNS VARCHAR(4096)
    DETERMINISTIC
    SQL SECURITY INVOKER
RETURN ROUND (   
        (
             CHAR_LENGTH(x)
            -  CHAR_LENGTH( REPLACE ( x, delim, "") ) 
        ) /  CHAR_LENGTH(delim)  )$$

