-- -----------------------------------------------------
-- View `kcson`.`_serv_activ`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `kcson`.`_serv_activ`;
USE `kcson`;
CREATE
 OR REPLACE DEFINER = 'root'@'localhost'
VIEW _serv_activ
AS
WITH `sa`
AS
(SELECT
*
    FROM `serv`
    
where
    `serv`.`year` = `Get_year`(`Get_WID`())
    
    ), `slvl`
AS
(SELECT
      `s`.`id` AS `id`,
      (SELECT
          TRIM(TRAILING ',' FROM (SELECT
              CONCAT(`s`.`id`, ',', COALESCE(GROUP_CONCAT(`s1`.`id` SEPARATOR ','), ''))
            FROM `sa` `s1`
            WHERE (`s1`.`sub_serv` = `s`.`id`)))) AS `lvl`,
      (SELECT
          TRIM(TRAILING ',' FROM (SELECT
              CONCAT(COALESCE(GROUP_CONCAT(`s2`.`id` SEPARATOR ','), ''))
            FROM (`sa` `s1`
              JOIN `sa` `s2`
                ON ((`s2`.`sub_serv` = `s1`.`id`)))
            WHERE (`s1`.`sub_serv` = `s`.`id`)))) AS `lvl2`,
      (SELECT
          TRIM(TRAILING ',' FROM CONCAT(COALESCE(GROUP_CONCAT(`s3`.`id` SEPARATOR ','), '')))
        FROM ((`sa` `s1`
          JOIN `sa` `s2`
            ON ((`s2`.`sub_serv` = `s1`.`id`)))
          JOIN `sa` `s3`
            ON ((`s3`.`sub_serv` = `s2`.`id`)))
        WHERE (`s1`.`sub_serv` = `s`.`id`)) AS `lvl3`,
      (SELECT
          TRIM(TRAILING ',' FROM CONCAT(COALESCE(GROUP_CONCAT(`s4`.`id` SEPARATOR ','), '')))
        FROM (((`sa` `s1`
          JOIN `sa` `s2`
            ON ((`s2`.`sub_serv` = `s1`.`id`)))
          JOIN `sa` `s3`
            ON ((`s3`.`sub_serv` = `s2`.`id`)))
          JOIN `sa` `s4`
            ON ((`s4`.`sub_serv` = `s3`.`id`)))
        WHERE (`s1`.`sub_serv` = `s`.`id`)) AS `lvl4`,
      (SELECT
          TRIM(TRAILING ',' FROM CONCAT(COALESCE(GROUP_CONCAT(`s5`.`id` SEPARATOR ','), '')))
        FROM ((((`sa` `s1`
          JOIN `sa` `s2`
            ON ((`s2`.`sub_serv` = `s1`.`id`)))
          JOIN `sa` `s3`
            ON ((`s3`.`sub_serv` = `s2`.`id`)))
          JOIN `sa` `s4`
            ON ((`s4`.`sub_serv` = `s3`.`id`)))
          JOIN `sa` `s5`
            ON ((`s5`.`sub_serv` = `s4`.`id`)))
        WHERE (`s1`.`sub_serv` = `s`.`id`)) AS `lvl5`
    FROM `sa` `s`), `sid_list`
AS
(SELECT
      `sa`.`id` AS `id`,
      TRIM(TRAILING ',' FROM CONCAT(`l`.`lvl`, ',', `l`.`lvl2`, ',', `l`.`lvl3`, ',', `l`.`lvl4`, ',', `l`.`lvl5`, ',')) AS `serv_id_list`
    FROM (`sa`
      JOIN `slvl` `l`
        ON ((`sa`.`id` = `l`.`id`))))
SELECT
  `s`.`id` AS `id`,
  `s`.`serv` AS `serv`,
  `s`.`serv_text` AS `serv_text`,
  `s`.`tnum` AS `tnum`,
  `s`.`price` AS `price`,
  `s`.`total` AS `total`,
  `s`.`sub_serv` AS `sub_serv`,
  `sl`.`serv_id_list` AS `serv_id_list`
FROM (`sa` `s`
  LEFT JOIN `sid_list` `sl`
    ON ((`sl`.`id` = `s`.`id`)));

