                    ORDER BY lmdate DESC
                    LIMIT 1) AS lm
        FROM
            add_info a
        INNER JOIN contracts c ON c.id = a.contracts_id
        INNER JOIN ripso r ON r.id = c.ripso_id
        WHERE
            r.servform_id IN (4)) t
            
