-- -----------------------------------------------------
-- View `kcson`.`_contr_has_serv_predv`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `kcson`.`_contr_has_serv_predv`;
USE `kcson`;
CREATE OR REPLACE 
    ALGORITHM = UNDEFINED 
    DEFINER = `root`@`localhost` 
    SQL SECURITY DEFINER
VIEW `_contr_has_serv_predv` AS
    SELECT 
		contracts_id,        
        (uslnum * price) AS `full_price`,
        f.tnum,
        serv,
        s.serv_text,
        uslnum,
        price,
        uslnum * price * perc AS to_pay,
        servform_id,
        serv_id,
        sub_serv,
        vdate,
        dep_id,
        perc,
        `year`
        
    from (
    SELECT 
    
		s.`year`,
        contracts_id,
        s.tnum,
        s.serv_text,
        serv,
        if (round(planned / r.months, 0) =0, 
			ceil(planned / r.months), round(planned / r.months, 0))  AS uslnum,
        price,
        servform_id,
        serv_id,
        sub_serv,
        `c`.`startdate` AS vdate,
        dep_id,
        (SELECT 
                `pd`.`perc`
            FROM
                `add_info` `pd`
            WHERE
                ((`pd`.`pddate` <= `c`.`startdate`)
                    AND (`pd`.`contracts_id` = `khs`.`contracts_id`))
            ORDER BY `pd`.`pddate` DESC
            LIMIT 1) AS perc
    FROM
        _contracts_has_serv khs
            JOIN
        contracts c ON c.id = khs.contracts_id
            JOIN
        serv s ON s.id = khs.serv_id
            JOIN
        ripso r ON r.id = c.ripso_id
            JOIN
        servform sf ON r.servform_id = sf.id) f
 
;

