from main
)
SELECT dep_id, contracts_id, serv_id,  year1, dep_has_worker_id

,sum(case  when month1 = 1 then uslnum else 0 end) as month1
,sum(case  when month1 = 2 then uslnum else 0 end) as month2
,sum(case  when month1 = 3 then uslnum else 0 end)as month3
,sum(case  when month1 = 4 then uslnum else 0 end)as month4
