,sum(case  when month1 = 7 then uslnum else 0 end)as month7
,sum(case  when month1 = 8 then uslnum else 0 end)as month8
,sum(case  when month1 = 9 then uslnum else 0 end)as month9
,sum(case  when month1 = 10 then uslnum else 0 end)as month10
,sum(case  when month1 = 11 then uslnum else 0 end)as month11
,sum(case  when month1 = 12 then uslnum else 0 end)as month12

from m1
group by dep_id, contracts_id, serv_id, dep_has_worker_id

;

-- -----------------------------------------------------
-- View `kcson`.`should_perc`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `kcson`.`should_perc`;
USE `kcson`;
CREATE  OR REPLACE VIEW `should_perc` AS
    SELECT 
        `t`.`contracts_id` AS `contracts_id`,
        `t`.`pddate` AS `pddate`,
        `t`.`ripso_id` AS `ripso_id`,
        `t`.`sdd` AS `sdd`,
        `t`.`servform_id` AS `servform_id`,
        `t`.`lm` AS `lm`,
        t.category_id,
        t.perc,
        (CASE
            WHEN
                t.category_id = 5
            THEN
                CASE
                    WHEN servform_id = 4 THEN 0.10
                    ELSE 0.5
                END
            ELSE CASE
                WHEN servform_id = 4 THEN 0.35
                ELSE (CASE
                    WHEN (`t`.`sdd` - (`t`.`lm` * 4)) > 0 THEN 0.4
                    WHEN (`t`.`sdd` - (`t`.`lm` * 3)) > 0 THEN 0.3
                    WHEN (`t`.`sdd` - (`t`.`lm` * 2.5)) > 0 THEN 0.2
                    WHEN (`t`.`sdd` - (`t`.`lm` * 2)) > 0 THEN 0.15
                    WHEN (`t`.`sdd` - (`t`.`lm` * 1.5)) > 0 THEN 0.1
                    ELSE 0
                END)
            END
        END) AS `should_perc`
    FROM
        (SELECT 
            `a`.`contracts_id` AS `contracts_id`,
                `c`.`ripso_id` AS `ripso_id`,
                `a`.`pddate` AS `pddate`,
                `a`.`sdd` AS `sdd`,
                `r`.`servform_id` AS `servform_id`,
                uhc.category_id,
                a.perc,
                (SELECT 
                        IF((`a`.`work_livemin` = 0), `live_min`.`live_min_p`, `live_min`.`live_min_w`) AS `live_min`
                    FROM
                        `live_min`
                    WHERE
                        (`a`.`pddate` > `live_min`.`lmdate`)
                    ORDER BY `live_min`.`lmdate` DESC
                    LIMIT 1) AS `lm`
        FROM
            `add_info` `a`
        LEFT JOIN `contracts` `c` ON ((`c`.`id` = `a`.`contracts_id`))
        LEFT JOIN `ripso` `r` ON ((`r`.`id` = `c`.`ripso_id`))
        LEFT JOIN `ufio` `u` ON ((`u`.`id` = `c`.`ufio_id`))
        LEFT JOIN ufio_has_category uhc ON ((`uhc`.`ufio_id` = `c`.`ufio_id`
            AND uhc.get_date < a.pddate
            AND uhc.category_id = 5))) t
    ORDER BY `t`.`pddate`;

